                                 <!-- Adicione outros campos conforme necessário -->
                                 <button type="submit" class="btn btn-primary btn-block">Enviar</button>
 
 
+                                <!-- Script JS para tratar a consulta e preencher os inputs -->
+<script>
+    document.getElementById('btnConsultar').addEventListener('click', function() {
+      // Obtém o valor digitado e remove caracteres não numéricos
+      let cnpjInput = document.getElementById('CNPJ_Busca').value;
+      let cnpj = cnpjInput.replace(/\D/g, '');
+      
+      // Validação simples: CNPJ deve ter 14 dígitos
+      if(cnpj.length !== 14) {
+        alert('CNPJ inválido');
+        return;
+      }
+      
+      // Exemplo de URL para a API Receitaws
+      let url = 'https://receitaws.com.br/v1/cnpj/' + cnpj;
+      
+      // Realiza a consulta com fetch
+      fetch(url)
+      .then(response => response.json())
+      .then(data => {
+        // Verifica se a API retornou erro
+        if(data.status && data.status !== "OK") {
+          alert(data.message || 'Erro na consulta');
+          return;
+        }
+        
+        // Preenche os campos do formulário com os dados retornados
+        document.getElementById('razaoSocial').value = data.nome || '';
+        document.getElementById('nomeFantasia').value = data.fantasia || '';
+        document.getElementById('dataInicio').value = data.abertura || '';
+        document.getElementById('telefone1').value = data.telefone || '';
+        document.getElementById('email').value = data.email || '';
+        document.getElementById('capitalSocial').value = data.capital_social || '';
+        document.getElementById('situacao').value = data.situacao || '';
+        
+        // Endereço
+        document.getElementById('logradouro').value = data.logradouro || '';
+        document.getElementById('numero').value = data.numero || '';
+        document.getElementById('complemento').value = data.complemento || '';
+        document.getElementById('bairro').value = data.bairro || '';
+        document.getElementById('municipio').value = data.municipio || '';
+        document.getElementById('uf').value = data.uf || '';
+        document.getElementById('cep').value = data.cep || '';
+        
+        // Simples Nacional e MEI
+        document.getElementById('simples').value = (data.simples && data.simples.optante) ? 'Sim' : 'Não';
+        document.getElementById('mei').value = (data.simei && data.simei.optante) ? 'Sim' : 'Não';
+        
+        // Atividades: atividade principal e secundárias
+        if(data.atividade_principal && data.atividade_principal.length > 0) {
+          let atividade = data.atividade_principal[0];
+          document.getElementById('atividadePrincipal').value = atividade.code + ' - ' + atividade.text;
+        }
+        if(data.atividades_secundarias && data.atividades_secundarias.length > 0) {
+          let secundarias = data.atividades_secundarias.map(item => item.code + ' - ' + item.text).join(', ');
+          document.getElementById('atividadesSecundarias').value = secundarias;
+        }
+        
+        // Sócios (caso haja, senão mostra 'Não informado')
+        if(data.qsa && data.qsa.length > 0) {
+          let socios = data.qsa.map(item => item.nome + ' (' + item.cnpj_cpf_do_socio + ')').join(', ');
+          document.getElementById('socios').value = socios;
+        } else {
+          document.getElementById('socios').value = 'Não informado';
+        }
+      })
+      .catch(error => {
+        console.error('Erro:', error);
+        alert('Erro na consulta');
+      });
+    });
+    </script>
 
+
+
                             </form>
 
                         </div>

