+}
+
+/* Imagem dentro do card */
+.custom-card img {
+  width: 100%;
+  height: 100%;
+  object-fit: cover;
+  transition: 0.4s ease-in-out;
+}
+
+/* Camada escura para hover */
+.custom-card::after {
+  content: "";
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  background: rgba(0, 0, 0, 0);
+  transition: background 0.4s ease-in-out;
+}
+
+/* Texto dentro do card */
+.custom-card .card-text {
+  position: absolute;
+  bottom: 15px;
+  left: 20px;
+  color: white;
+  font-size: 1.2rem;
+  font-weight: bold;
+  z-index: 2;
+}
+
+/* Efeito hover: escurece a imagem */
+.custom-card:hover::after {
+  background: rgba(0, 0, 0, 0.5);
+}
