+  padding: 40px;
+  margin-left: 15%;
+}
+.cadastro-imagem {
+  position: relative;
+  overflow: hidden;
+}
+.cadastro-imagem img {
+  width: 100%;
+  display: block;
+  transition: filter 0.3s, box-shadow 0.3s;
+}
+.cadastro-imagem:hover img {
+  filter: brightness(70%);
+  box-shadow: 0 0 26px -12px #aa0000;
+}
+
+/* Container de Benefícios */
+.benefits-container {
