+}
+.benefit-item:hover .benefit-icon {
+  transform: scale(1.1);
+}
+.benefit-item:hover .benefit-text h3 {
+  color: #aa0000;
+}
+.benefit-text h3 {
+  font-size: 1.2rem;
+  font-weight: bold;
+}
+.benefit-text p {
+  font-size: 0.9rem;
+}
