+  .benefit-item {
+    flex-direction: column;
+    text-align: center;
+  }
+  .benefit-icon {
+    width: 40px;
+    height: 40px;
+    margin-bottom: 2em;
+  }
+}
+
+/* Imagem com efeito hover */
+.hover-effect-image {
+  transition: filter 0.3s;
+}
+.hover-effect-image:hover {
+  filter: brightness(50%);
+}
+
+/* Imagem Cashback */
+.bg-image-cashback {
+  position: relative;
+  overflow: hidden;
+}
